import { GoogleGenAI } from '@google/genai';
import React, { useEffect, useMemo, useRef, useState } from 'react';

// Simple helper to read possible reply shapes from SDK responses
function extractReply(resp) {
  if (!resp) return '[No content]';
  // common SDK shapes
  if (typeof resp === 'string') return resp;
  if (resp.text) return resp.text;
  if (resp.output_text) return resp.output_text;
  if (resp.candidates && resp.candidates.length) {
    const c = resp.candidates[0];
    if (c.text) return c.text;
    if (c.message && c.message.content) {
      // content can be array of parts
      return c.message.content.map(p => p.text || '').join('');
    }
    if (c.content) {
      return (Array.isArray(c.content) ? c.content.map(p => p.text || '').join('') : String(c.content));
    }
  }
  // fallback
  try { return JSON.stringify(resp); } catch { return String(resp); }
}

export default function AItest({
  defaultModel = 'gemini-2.5-flash',
  starter = 'Âó®ÔºÅÂπ´ÊàëÊ∏¨Ë©¶‰∏Ä‰∏ãÂè∞ÂåóÊóÖÈÅäÁöÑ‰∏ÄÊó•Ë°åÁ®ãÔΩû',
}) {
  const [model, setModel] = useState(defaultModel);
  const [history, setHistory] = useState([]);
  const [input, setInput] = useState('');
  const [apiKey, setApiKey] = useState('');
  const [rememberKey, setRememberKey] = useState(true);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const listRef = useRef(null);

  // Load key from localStorage (for demo only ‚Äî never ship an exposed key in production)
  useEffect(() => {
    const saved = localStorage.getItem('gemini_api_key');
    if (saved) setApiKey(saved);
  }, []);

  // Warm welcome + starter
  useEffect(() => {
    setHistory([{ role: 'model', parts: [{ text: 'üëã ÈÄôË£°ÊòØ Gemini Â∞èÂπ´ÊâãÔºåÊúâ‰ªÄÈ∫ºÊÉ≥ËÅäÁöÑÔºü' }] }]);
    if (starter) setInput(starter);
  }, [starter]);

  // auto-scroll to bottom
  useEffect(() => {
    const el = listRef.current;
    if (!el) return;
    el.scrollTop = el.scrollHeight;
  }, [history, loading]);

  const ai = useMemo(() => {
    try {
      return apiKey ? new GoogleGenAI({ apiKey }) : null;
    } catch {
      return null;
    }
  }, [apiKey]);

  async function sendMessage(message) {
    const content = (message ?? input ?? '').trim();
    if (!content || loading) return;
    if (!ai) {
      setError('Ë´ãÂÖàËº∏ÂÖ•ÊúâÊïàÁöÑ Gemini API Key');
      return;
    }

    setError('');
    setLoading(true);

    const newHistory = [...history, { role: 'user', parts: [{ text: content }] }];
    setHistory(newHistory);
    setInput('');

    try {
      // Use the official SDK directly in the browser
      const resp = await ai.models.generateContent({
        model,
        contents: newHistory, // send the chat history to keep context
      });

      const reply = extractReply(resp) || '[No content]';
      setHistory(h => [...h, { role: 'model', parts: [{ text: reply }] }]);
    } catch (err) {
      setError(err?.message || String(err));
    } finally {
      setLoading(false);
    }
  }

  function renderMarkdownLike(text) {
    const lines = text.split(/\n/);
    return (
      <>
        {lines.map((ln, i) => (
          <div key={i} style={{ whiteSpace: 'pre-wrap', wordBreak: 'break-word' }}>{ln}</div>
        ))}
      </>
    );
  }

  return (
    <div style={styles.wrap}>
      <div style={styles.card}>
        <div style={styles.header}>Gemini ChatÔºàÁõ¥ÈÄ£ SDKÔºå‰∏çÁ∂ì proxyÔºâ</div>

        {/* Controls */}
        <div style={styles.controls}>
          <label style={styles.label}>
            <span>Model</span>
            <input
              value={model}
              onChange={e => setModel(e.target.value)}
              placeholder="‰æãÂ¶Ç gemini-2.5-flash„ÄÅgemini-2.5-pro"
              style={styles.input}
            />
            <div style={{ fontSize: 12, opacity: 0.7, marginTop: 4 }}>
              Ê®°ÂûãÂêçÁ®±ÊúÉÈö®ÊôÇÈñìÊõ¥Êñ∞ÔºåËã•ÈåØË™§Ë´ãÊîπÊàêÂÆòÊñπÊ∏ÖÂñÆ‰∏≠ÁöÑÊúâÊïà ID„ÄÇ
            </div>
          </label>

          <label style={styles.label}>
            <span>Gemini API Key</span>
            <input
              type="password"
              value={apiKey}
              onChange={(e) => {
                const v = e.target.value; setApiKey(v);
                if (rememberKey) localStorage.setItem('gemini_api_key', v);
              }}
              placeholder="Ë≤º‰∏ä‰Ω†ÁöÑ API KeyÔºàÂè™Âú®Êú¨Ê©üÁÄèË¶ΩÂô®ÂÑ≤Â≠òÔºâ"
              style={styles.input}
            />
            <label style={{ display:'flex', alignItems:'center', gap:8, marginTop:6, fontSize:12 }}>
              <input type="checkbox" checked={rememberKey} onChange={(e)=>{
                setRememberKey(e.target.checked);
                if (!e.target.checked) localStorage.removeItem('gemini_api_key');
                else if (apiKey) localStorage.setItem('gemini_api_key', apiKey);
              }} />
              <span>Ë®ò‰ΩèÂú®Êú¨Ê©üÔºàlocalStorageÔºâ</span>
            </label>
            <div style={{ fontSize: 12, opacity: 0.7, marginTop: 4 }}>
              Demo Áî®Ê≥ïÔºöÂú®ÁÄèË¶ΩÂô®ÂÖß‰øùÂ≠ò Key ÂÉÖ‰æõÊïôÂ≠∏„ÄÇÊ≠£ÂºèÁí∞Â¢ÉË´ãÊîπËµ∞ÂæåÁ´ØÊàñ‰ΩøÁî®ÂÆâÂÖ®ÈôêÂà∂ÁöÑ Key„ÄÇ
            </div>
          </label>
        </div>

        {/* Messages */}
        <div ref={listRef} style={styles.messages}>
          {history.map((m, idx) => (
            <div key={idx} style={{ ...styles.msg, ...(m.role === 'user' ? styles.user : styles.assistant) }}>
              <div style={styles.msgRole}>{m.role === 'user' ? 'You' : 'Gemini'}</div>
              <div style={styles.msgBody}>{renderMarkdownLike(m.parts.map(p => p.text).join('\n'))}</div>
            </div>
          ))}
          {loading && (
            <div style={{ ...styles.msg, ...styles.assistant }}>
              <div style={styles.msgRole}>Gemini</div>
              <div style={styles.msgBody}>ÊÄùËÄÉ‰∏≠‚Ä¶</div>
            </div>
          )}
        </div>

        {/* Error */}
        {error && (
          <div style={styles.error}>‚ö† {error}</div>
        )}

        {/* Composer */}
        <form
          onSubmit={e => { e.preventDefault(); sendMessage(); }}
          style={styles.composer}
        >
          <input
            placeholder="Ëº∏ÂÖ•Ë®äÊÅØÔºåÊåâ Enter ÈÄÅÂá∫"
            value={input}
            onChange={e => setInput(e.target.value)}
            style={styles.textInput}
          />
          <button type="submit" disabled={loading || !input.trim() || !apiKey} style={styles.sendBtn}>
            ÈÄÅÂá∫
